package com.frysning.springdnd.action;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;@Componentpublic class ActionByIdConverter implements Converter<String, Action> {    private ActionRepository actionRepository;    @Autowired    public ActionByIdConverter(ActionRepository actionRepository) {        this.actionRepository = actionRepository;    }    @Override    public Action convert(String id) {        Long longId = Long.parseLong(id);        Optional<Action> optionalSpeed = actionRepository.findById(longId);        return optionalSpeed.orElse(new Action());    }}