package com.frysning.springdnd.action_type;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;@Componentpublic class ActionTypeByIdConverter implements Converter<String, ActionType> {    private ActionTypeRepository damageRepository;    @Autowired    public ActionTypeByIdConverter(ActionTypeRepository damageRepository) {        this.damageRepository = damageRepository;    }    @Override    public ActionType convert(String id) {        Long longId = Long.parseLong(id);        Optional<ActionType> optionalSpeed = damageRepository.findById(longId);        return optionalSpeed.orElse(new ActionType());    }}