package com.frysning.springdnd.challengerating;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;@Entity@Table(name = "challengeRating")public class ChallengeRating {    private @Id    @GeneratedValue(strategy = GenerationType.IDENTITY) Long id;    private String rating;    private int xp;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getRating() {        return rating;    }    public void setRating(String rating) {        this.rating = rating;    }    public int getXp() {        return xp;    }    public void setXp(int xp) {        this.xp = xp;    }}