package com.frysning.springdnd.components;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonFormat.Shape;import org.springframework.util.StringUtils;@JsonFormat(shape = Shape.OBJECT)public enum Component {    VERBAL(1, "V"),    SOMATIC(1,"S" ),    MATERIAL(2,"M"),    NOT_SUPPORTED(0);    public final int id;    private String description;    Component(int id) {        this.id = id;    }    public static Component getById(int id) {		return switch (id) {			case 1 -> VERBAL;			case 2 -> SOMATIC;			case 3 -> MATERIAL;			default -> NOT_SUPPORTED;		};    }    public int getId() {        return id;    }    public String getComponent() {        return StringUtils.capitalize(name().toLowerCase());    }    public String getDescription() { return description;}    Component(int id, String description) {        this.description = description;        this.id = id;    }}