package com.frysning.springdnd.conditions;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;@Entity@Table(name = "condition")public class Condition {    private @Id    @GeneratedValue(strategy = GenerationType.IDENTITY) Long id;    private String name;    @Column(length = 800)    private String description;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    @JsonIgnore    public String getInternalDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public List<String> getDescription() {        return Arrays                .stream(description.split("-"))                .filter(s -> !s.isEmpty() || !s.isBlank())                .collect(Collectors.toList());    }}