package com.frysning.springdnd.conditions;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import java.util.Optional;@Componentpublic class ConditionTypeByIdConverter implements Converter<String, ConditionType> {    private final ConditionTypeRepository conditionTypeRepository;    @Autowired    public ConditionTypeByIdConverter(ConditionTypeRepository conditionTypeRepository) {        this.conditionTypeRepository = conditionTypeRepository;    }    @Override    public ConditionType convert(String id) {        System.out.println("Condition ID: " + id);        Long longId = Long.parseLong(id);        Optional<ConditionType> optionalCondition = conditionTypeRepository.findById(longId);        System.out.println("Condition is found: " + optionalCondition.isPresent());        return optionalCondition.orElse(new ConditionType());    }}