package com.frysning.springdnd.damage_type;import javax.persistence.*;import java.util.Objects;@Entity@Table(name = "damage_type")public class DamageType {	private @Id	@GeneratedValue(strategy = GenerationType.IDENTITY) Long id;	private String name;	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public DamageType() {	}	public DamageType(Long id, String name) {		this.name = name;		this.id = id;	}	public boolean equals(DamageType dt) {		return (dt.getName().equals(this.name) || Objects.equals(dt.id, this.id));	}}