package com.frysning.springdnd.damage_type;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;@Componentpublic class DamageTypeByIdConverter implements Converter<String, DamageType> {    private DamageTypeRepository damageRepository;    @Autowired    public DamageTypeByIdConverter(DamageTypeRepository damageRepository) {        this.damageRepository = damageRepository;    }    @Override    public DamageType convert(String id) {        System.out.println("Speed ID: " + id);        Long longId = Long.parseLong(id);        Optional<DamageType> optionalSpeed = damageRepository.findById(longId);        System.out.println("Speed is found: " + optionalSpeed.isPresent());        return optionalSpeed.orElse(new DamageType());    }}