package com.frysning.springdnd.effects;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.annotation.JsonProperty;import javax.persistence.*;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;@Entity@Table(name = "effect")public class Effect {	@JsonProperty(index = 1)	private @Id	@GeneratedValue(strategy = GenerationType.IDENTITY) Long id;	@JsonProperty(index = 2)	private String name;	@Column(length = 200)	private String description;	public Effect() {	}	public Effect(Long id, String name, String description) {		this.id = id;		this.name = name;		this.description = description;	}	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public boolean equals(Effect con) {		return (con.getName().equals(this.getName()) || con.getId().equals(this.getId()));	}}