package com.frysning.springdnd.effects;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import java.util.Optional;@Componentpublic class EffectByIdConverter implements Converter<String, Effect> {    private final EffectRepository effectRepository;    @Autowired    public EffectByIdConverter(EffectRepository effectRepository) {        this.effectRepository = effectRepository;    }    @Override    public Effect convert(String id) {        System.out.println("Effect ID: " + id);        Long longId = Long.parseLong(id);        Optional<Effect> optionalCondition = effectRepository.findById(longId);        System.out.println("Effect is found: " + optionalCondition.isPresent());        return optionalCondition.orElse(new Effect());    }}