package com.frysning.springdnd.enemy;import com.frysning.springdnd.modifier_type.ModifierType;import com.frysning.springdnd.stats.ReadableStats;public class CalculatedSavingThrow {    int id;    String name;    int value = 0;    public CalculatedSavingThrow(ModifierType modifierType, ReadableStats readableStats,        int proficiencyBonus) {        switch (modifierType) {            case STR:                value = readableStats.getStrength().getModifier();                break;            case DEX:                value = readableStats.getDexterity().getModifier();                break;            case CON:                value = readableStats.getConstitution().getModifier();                break;            case INT:                value = readableStats.getIntelligence().getModifier();                break;            case WIS:                value = readableStats.getWisdom().getModifier();                break;            case CHA:                value = readableStats.getCharisma().getModifier();                break;            case NOT_SUPPORTED:                value -= proficiencyBonus;                break;        }        value += proficiencyBonus;        id = modifierType.getId();        name = modifierType.name();    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getValue() {        return value;    }    public void setValue(int value) {        this.value = value;    }}