package com.frysning.springdnd.language;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;@Componentpublic class LanguageByIdConverter implements Converter<String, Language> {    private LanguageRepository languageRepository;    @Autowired    public LanguageByIdConverter(LanguageRepository languageRepository) {        this.languageRepository = languageRepository;    }    @Override    public Language convert(String id) {        Long longId = Long.parseLong(id);        Optional<Language> optionalSpeed = languageRepository.findById(longId);        return optionalSpeed.orElse(new Language());    }}