package com.frysning.springdnd.magic_school;import com.fasterxml.jackson.annotation.JsonProperty;import javax.persistence.*;@Entity@Table(name = "magic_school")public class MagicSchool {    @JsonProperty(index = 1)    private @Id    @GeneratedValue(strategy = GenerationType.IDENTITY) Long id;     @JsonProperty(index = 2)    private String name;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }}