package com.frysning.springdnd.modifier_type;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonFormat.Shape;@JsonFormat(shape = Shape.OBJECT)public enum ModifierType {    STR(1),    DEX(2),    CON(3),    INT(4),    WIS(5),    CHA(6),    NOT_SUPPORTED(0);;    public final int id;    public final String modifier = name();    ModifierType(int id) {        this.id = id;    }    public static ModifierType getById(int id) {        ModifierType modifierType = NOT_SUPPORTED;        switch (id) {            case 1:                modifierType = STR;                break;            case 2:                modifierType = DEX;                break;            case 3:                modifierType = CON;                break;            case 4:                modifierType = INT;                break;            case 5:                modifierType = WIS;                break;            case 6:                modifierType = CHA;                break;        }        return modifierType;    }    public int getId() {        return id;    }    public String getModifier() {        return modifier;    }}