package com.frysning.springdnd.modifiertype;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonFormat.Shape;@JsonFormat(shape = Shape.OBJECT)public enum ModifierType {    STR(1),    DEX(2),    CON(3),    INT(4),    WIS(5),    CHA(6),    NOT_SUPPORTED(0);;    public final int id;    public final String modifier = name();    ModifierType(int id) {        this.id = id;    }    public static ModifierType getById(int id) {        return switch (id) {            case 1 -> STR;            case 2 -> DEX;            case 3 -> CON;            case 4 -> INT;            case 5 -> WIS;            case 6 -> CHA;            default -> NOT_SUPPORTED;        };    }    public int getId() {        return id;    }    public String getModifier() {        return modifier;    }}