package com.frysning.springdnd.size;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonFormat.Shape;import org.springframework.util.StringUtils;@JsonFormat(shape = Shape.OBJECT)public enum Size {    TINY(1,"anything less that 1 feet/ 30cm tall." ),    SMALL(2,"1 to 3 ft/30cm to 92cm tall."),    MEDIUM(3,"3' to 10' ft/ 92cm to 3.05m tall. (average adult humans have a height of 5.6'/1.7m)"),    LARGE(4,"10' to 15' ft/ 3.05m to 4.6m tall."),    HUGE(5,"15' to 20' ft/ 4.6m to 6.10m tall."),    GARGANTUAN(6, "20'/ 6.10m or more."),    KAIJU(7,"50'/ 15.24m more."),    NOT_SUPPORTED(0);    public final int id;    private String description;    Size(int id) {        this.id = id;    }    public static Size getById(int id) {        Size size = NOT_SUPPORTED;        switch (id) {            case 1:                size = TINY;                break;            case 2:                size = SMALL;                break;            case 3:                size = MEDIUM;                break;            case 4:                size = LARGE;                break;            case 5:                size = HUGE;                break;            case 6:                size = GARGANTUAN;                break;            case 7:                size = KAIJU;                break;        }        return size;    }    public int getId() {        return id;    }    public String getSize() {        return StringUtils.capitalize(name().toLowerCase());    }    public String getDescription() { return description;}    Size(int id, String description) {        this.description = description;        this.id = id;    }}