package com.frysning.springdnd.size;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonFormat.Shape;import org.springframework.util.StringUtils;@JsonFormat(shape = Shape.OBJECT)public enum Size {    TINY(1),    SMALL(2),    MEDIUM(3),    LARGE(4),    HUGE(5),    GARGANTUAN(6),    KAIJU(7),    NOT_SUPPORTED(0);    public final int id;    Size(int id) {        this.id = id;    }    public static Size getById(int id) {        return switch (id) {            case 1 -> TINY;            case 2 -> SMALL;            case 3 -> MEDIUM;            case 4 -> LARGE;            case 5 -> HUGE;            case 6 -> GARGANTUAN;            case 7 -> KAIJU;            default -> NOT_SUPPORTED;        };    }    public int getId() {        return id;    }    public String getSize() {        return StringUtils.capitalize(name());    }}