package com.frysning.springdnd.speed;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;@Componentpublic class SpeedByIdConverter implements Converter<String, Speed> {    private SpeedRepository speedRepository;    @Autowired    public SpeedByIdConverter(SpeedRepository speedRepository) {        this.speedRepository = speedRepository;    }    @Override    public Speed convert(String id) {        Long longId = Long.parseLong(id);        Optional<Speed> optionalSpeed = speedRepository.findById(longId);        return optionalSpeed.orElse(new Speed());    }}