package com.frysning.springdnd.speed;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;@Componentpublic class SpeedValueByIdConverter implements Converter<String, SpeedValue> {    private SpeedValueRepository speedValueRepository;    @Autowired    public SpeedValueByIdConverter(SpeedValueRepository speedValueRepository) {        this.speedValueRepository = speedValueRepository;    }    @Override    public SpeedValue convert(String id) {        Long longId = Long.parseLong(id);        Optional<SpeedValue> optionalSpeed = speedValueRepository.findById(longId);        return optionalSpeed.orElse(new SpeedValue());    }}