package com.frysning.springdnd.speed_type;import com.fasterxml.jackson.annotation.JsonProperty;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;@Entity@Table(name = "speed_type")public class SpeedType {    public SpeedType(){    }    public SpeedType(Long id, String name) {        this.id = id;        this.name = name;    }    @JsonProperty(index = 1)    private @Id    @GeneratedValue(strategy = GenerationType.IDENTITY) Long id;     @JsonProperty(index = 2)    private String name;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }}