package com.frysning.springdnd.stats;public class CalculatedStat {    int baseStat;    int racialBonus = 0;    int modifier;    public CalculatedStat(int baseStat) {        this.baseStat = baseStat;        calculateBonus(baseStat);    }    public CalculatedStat(int baseStat, int racialBonus) {        this.baseStat = baseStat;        this.racialBonus = racialBonus;        calculateBonus(baseStat + racialBonus);    }    private void calculateBonus(int stat) {        this.modifier = (stat / 2) - 5;    }    public int getBaseStat() {        return baseStat;    }    public int getModifier() {        return modifier;    }}