package com.frysning.springdnd.combiner;import com.frysning.springdnd.damage_type.DamageType;import com.frysning.springdnd.enemy.Enemy;import com.frysning.springdnd.race.Race;import org.junit.jupiter.api.Test;import java.util.Arrays;import java.util.List;import static org.hamcrest.MatcherAssert.assertThat;import static org.hamcrest.Matchers.containsInAnyOrder;public class CombinerDamageTests {	DamageType fire = new DamageType(1L, "Fire");	DamageType ice = new DamageType(2L, "Ice");	DamageType water = new DamageType(3L, "Water");	DamageType lighting = new DamageType(4L, "Lighting");	DamageType sound = new DamageType(5L, "Sound");	DamageType earth = new DamageType(6L, "Earth");	DamageType piercing = new DamageType(6L, "Piercing");	DamageType slashing = new DamageType(6L, "Slash");	@Test	public void combinedWeaknessCreatesCorrectList() {		List<DamageType> testList = Arrays.asList(fire, ice, water);		List<DamageType> enemyWeakness = Arrays.asList(ice, water);		List<DamageType> raceWeakness = List.of(fire, sound, water);		List<DamageType> raceResistance = List.of(lighting);		List<DamageType> enemyResistance = List.of();		List<DamageType> enemyImmunity = List.of(sound);		List<DamageType> raceImmunity = List.of();		Race race = new Race();		race.setDamageWeakness(raceWeakness);		race.setDamageResistance(raceResistance);		race.setDamageImmunity(raceImmunity);		Enemy enemy = new Enemy();		enemy.setDamageWeakness(enemyWeakness);		enemy.setDamageResistance(enemyResistance);		enemy.setDamageImmunity(enemyImmunity);		var combined = new Combined(enemy, race);		var combinedWeakness = combined.getWeaknessToDamage();		assertThat("List equality without order",				testList, containsInAnyOrder(combinedWeakness.toArray()));	}	@Test	public void combinedResistanceCreatesCorrectList() {		List<DamageType> testList = Arrays.asList(sound, fire);		List<DamageType> enemyWeakness = Arrays.asList(sound, water);		List<DamageType> raceWeakness = List.of();		List<DamageType> raceResistance = List.of(water);		List<DamageType> enemyResistance = List.of(fire);		List<DamageType> raceImmunity = List.of(sound);		List<DamageType> enemyImmunity = List.of();		Race race = new Race();		race.setDamageWeakness(raceWeakness);		race.setDamageResistance(raceResistance);		race.setDamageImmunity(raceImmunity);		Enemy enemy = new Enemy();		enemy.setDamageWeakness(enemyWeakness);		enemy.setDamageResistance(enemyResistance);		enemy.setDamageImmunity(enemyImmunity);		var combined = new Combined(enemy, race);		var combinedResistance = combined.getResistanceToDamage();		assertThat("List equality without order",				testList, containsInAnyOrder(combinedResistance.toArray()));	}	@Test	public void combinedImmunityCreatesCorrectList() {		List<DamageType> testList = Arrays.asList(fire, water);		List<DamageType> enemyWeakness = List.of(sound);		List<DamageType> raceWeakness = List.of(ice);		List<DamageType> raceResistance = List.of();		List<DamageType> enemyResistance = List.of(water);		List<DamageType> enemyImmunity = List.of(fire, ice);		List<DamageType> raceImmunity = List.of(water, fire, sound);		Race race = new Race();		race.setDamageWeakness(raceWeakness);		race.setDamageResistance(raceResistance);		race.setDamageImmunity(raceImmunity);		Enemy enemy = new Enemy();		enemy.setDamageWeakness(enemyWeakness);		enemy.setDamageResistance(enemyResistance);		enemy.setDamageImmunity(enemyImmunity);		var combined = new Combined(enemy, race);		var combinedImmunity = combined.getImmunityToDamage();		assertThat("List equality without order",				testList, containsInAnyOrder(combinedImmunity.toArray()));	}	@Test	public void combinedDataForDamageTypeIsCorrect() {		List<DamageType> weaknessList = List.of(slashing, piercing);		List<DamageType> resistList = List.of(earth, sound);		List<DamageType> immunlist = List.of(fire, water);		List<DamageType> enemyWeakness = List.of(slashing, earth);		List<DamageType> raceWeakness = List.of(piercing, lighting);		List<DamageType> raceResistance = List.of(lighting, sound);		List<DamageType> enemyResistance = List.of(water);		List<DamageType> enemyImmunity = List.of(fire);		List<DamageType> raceImmunity = List.of(earth, water);		Race race = new Race();		race.setDamageWeakness(raceWeakness);		race.setDamageResistance(raceResistance);		race.setDamageImmunity(raceImmunity);		Enemy enemy = new Enemy();		enemy.setDamageWeakness(enemyWeakness);		enemy.setDamageResistance(enemyResistance);		enemy.setDamageImmunity(enemyImmunity);		var combined = new Combined(enemy, race);		var combinedImmunity = combined.getImmunityToDamage();		var combinedWeakness = combined.getWeaknessToDamage();		var combinedResistance = combined.getResistanceToDamage();		assertThat("combinedImmunity equality without order",				immunlist, containsInAnyOrder(combinedImmunity.toArray()));		assertThat("combinedWeakness equality without order",				weaknessList, containsInAnyOrder(combinedWeakness.toArray()));		assertThat("combinedResistance equality without order",				resistList, containsInAnyOrder(combinedResistance.toArray()));	}}